
public class P2if_else {
	//WAP print a number only if it is multiply of 5 & it should be an even number

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int num=22;
	if (num%2==0 && num%5==0 ) {
		System.out.println(num);
	}
	else {
		System.out.println("not the desired number ");
	}
	}

}
